    <!-- Need: Apexcharts -->
    <script src="../assets/extensions/apexcharts/apexcharts.min.js"></script>
    <script src="../assets/static/js/pages/dashboard.js"></script>
    <script>
        document.addEventListener('DOMContentLoaded', function() {
            // --- Data Panen dari PHP ---
            const panenData = <?= json_encode($panenCounts) ?>;
            const panenLabels = <?= json_encode($panenLabels) ?>;

            const optionsPanen = {
                chart: {
                    type: 'bar',
                    width: '100%',
                    height: '100%',
                    toolbar: {
                        show: false
                    }
                },
                series: [{
                    name: 'Jumlah Panen',
                    data: panenData
                }],
                xaxis: {
                    categories: panenLabels
                },
                yaxis: {
                    title: {
                        text: ''
                    },
                    min: 0,
                    labels: {
                        show: false
                    }, // sembunyikan label Y jika perlu
                    axisBorder: {
                        show: false
                    }, // opsi: sembunyikan garis tenggorokan Y
                    axisTicks: {
                        show: false
                    } // opsi: sembunyikan tanda tick Y
                },
                dataLabels: {
                    enabled: false
                },
                fill: {
                    opacity: 0.8
                },
                colors: ['#435ebe'],
                responsive: [{
                    breakpoint: 768,
                    options: {
                        chart: {
                            height: 250
                        }
                    }
                }]
            };

            const chartPanen = new ApexCharts(
                document.querySelector('#chart-panen-perbulan'),
                optionsPanen
            );
            chartPanen.render();

            // --- Data Losses dari PHP ---
            const lossesData = <?= json_encode($lossesCounts) ?>;
            const lossesLabels = panenLabels; // label bulan sama

            const optionsLosses = {
                chart: {
                    type: 'bar',
                    width: '100%',
                    height: '100%',
                    toolbar: {
                        show: false
                    }
                },
                series: [{
                    name: 'Tanaman Losses',
                    data: lossesData
                }],
                xaxis: {
                    categories: lossesLabels
                },
                yaxis: {
                    title: {
                        text: ''
                    },
                    min: 0,
                    labels: {
                        show: false
                    },
                    axisBorder: {
                        show: false
                    },
                    axisTicks: {
                        show: false
                    }
                },
                dataLabels: {
                    enabled: false
                },
                fill: {
                    opacity: 0.8
                },
                colors: ['#dc3545'], // warna merah untuk losses
                responsive: [{
                    breakpoint: 768,
                    options: {
                        chart: {
                            height: 250
                        }
                    }
                }]
            };

            const chartLosses = new ApexCharts(
                document.querySelector('#chart-losses-perbulan'),
                optionsLosses
            );
            chartLosses.render();
        });
    </script>
</body>

